import React, { useState, useEffect } from 'react';

const Recommend = () => {
  const [userInput, setUserInput] = useState('');
  const [recommendations, setRecommendations] = useState([]);
  const [country, setCountry] = useState('');

  const fetchRecommendations = async () => {
    try {
      const response = await fetch('http://localhost:3002/recommend', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userInput: userInput,
          country: country
        }),
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const data = await response.json();
      setRecommendations(data);
    } catch (error) {
      console.error('Error fetching recommendations:', error);
    }
  };

  useEffect(() => {
    // Fetch recommendations when the component mounts or country changes
    fetchRecommendations();
  }, [userInput, country, fetchRecommendations]); // Include fetchRecommendations in the dependency array

  const handleChange = (e) => {
    setUserInput(e.target.value);
  };

  const handleCountryChange = (e) => {
    setCountry(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Fetch recommendations again on form submission
    fetchRecommendations();
  };

  return (
    <div className="recommend">
      <h1>Travel Destination Recommender</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="interests">Enter your interests:</label>
        <textarea
          id="interests"
          rows={4} // Set the number of rows to 4
          value={userInput}
          onChange={handleChange}
        />
        <label htmlFor="country">Enter country:</label>
        <input
          type="text"
          id="country"
          value={country}
          onChange={handleCountryChange}
        />
        <button type="submit">Get Recommendations</button>
      </form>
      <div>
        <h2>Recommended Destinations:</h2>
        <ul>
          {recommendations.length > 0 ? (
            recommendations.map((destination, index) => (
              <li key={index}>{destination.name}, {destination.country}</li>
            ))
          ) : (
            <li>No recommendations available. Please try again.</li>
          )}
        </ul>
      </div>
    </div>
  );
};

export default Recommend;
