import React, { useState } from "react";
import ResponsiveAppBar from "../components/Navbar";
import img from "../assets/person-traveling-enjoying-their-vacation.webp";
import TextField from "@mui/material/TextField";
import LocationOnIcon from "@mui/icons-material/LocationOn";
import FavoriteIcon from "@mui/icons-material/Favorite";
import CountrySelect from "../components/CountrySearch";
import MoodIcon from "@mui/icons-material/Mood";
import Button from "@mui/material/Button";
import RecommendIcon from '@mui/icons-material/Recommend';
import { Card, Dialog, DialogTitle, DialogContent, DialogActions } from "@mui/material";

function Home() {
  const [tags, setTags] = useState([]);
  const [recommendations, setRecommendations] = useState([]);
  const [userInput, setUserInput] = useState('');
  const [country, setCountry] = useState('');
  const [selectedDestination, setSelectedDestination] = useState(null);
  const [showCards, setShowCards] = useState(false); // State to toggle visibility of the div containing cards

  const addTag = (e) => {
    if (e.key === "Enter") {
      if (e.target.value.length > 0) {
        setTags([...tags, e.target.value]);
        e.target.value = "";
      }
    }
  };

  const removeTag = (removedTag) => {
    const newTags = tags.filter((tag) => tag !== removedTag);
    setTags(newTags);
  };

  const handleSubmit = async () => {
    try {
      const tagsString = tags.join(',');

      const response = await fetch('http://localhost:3002/recommend', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userInput: userInput,
          country: country
          //tags: tagsString,
        }),
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const data = await response.json();
      setRecommendations(data);
      setShowCards(true); // Show the div containing cards
    } catch (error) {
      console.error('Error fetching recommendations:', error);
    }
  };

  const handleChange = (e) => {
    setUserInput(e.target.value);
  };

  const handleCardClick = (destination) => {
    setSelectedDestination(destination);
  };

  const handleCloseDialog = () => {
    setSelectedDestination(null);
  };

  return (
    <div className="h-screen overflow-hidden px">
      <div
        className="w-full h-full bg-cover bg-center fixed"
        style={{ backgroundImage: `url(${img})` }}
      ></div>

      <div className="absolute inset-0 overflow-auto">
        <div className="flex flex-col h-full">
          <div className="h-1/6">
            <ResponsiveAppBar />
          </div>

          <div className="h-4/6 flex flex-col justify-center">
            <div>
              <h1 className="text-white text-6xl">Dreaming of New Horizons? </h1>
              <h4 className="text-white text-xl pl-[2px] pt-4">
                Checkout Our New AI Travel Recommendation System!
              </h4>
            </div>
          </div>

          <div className="h-[9rem] rounded-3xl bg-white py-5" style={{ backgroundColor: 'rgba(255, 255, 255, 0.8)' }}>
            <div className="flex h-full">
              <div className="flex w-1/4 border-r-2 h-full border-black border-opacity-30 gap-5 justify-center items-center">
                <div className="mt-4">
                  <LocationOnIcon />
                </div>

                <div>
                  <CountrySelect onChange={(selectedCountry) => setCountry(selectedCountry)} />
                </div>
              </div>

              <div className="flex w-1/4 border-r-2 h-full border-black border-opacity-30 px-5 overflow-y-auto gap-5 items-center">
                <div className="mt-5">
                  <MoodIcon />
                </div>
                <TextField
                  className="w-full border"
                  id="standard-textarea"
                  label="Describe your preference"
                  placeholder="eg: I want to travel to historical places and do some hiking and surfing"
                  multiline
                  rows='2'
                  variant="standard"
                  onChange={handleChange}
                />
              </div>

              <div className="flex w-1/4 border-r-2 h-full border-black border-opacity-30 px-12 mb-8 gap-3 items-center">
                <div className="mt-5">
                  <FavoriteIcon />
                </div>

                <div className="w-full pt-2 overflow-y-auto">
                  <div className="w-full max-w-75 flex flex-wrap min-h-30 border-b border-gray-400 py-2">
                    {tags.map((tag, index) => (
                      <div
                        key={index}
                        className="flex items-center p-1 border border-gray-300 rounded-md mr-2 mb-2"
                      >
                        {tag}{" "}
                        <span
                          className="ml-2 cursor-pointer"
                          onClick={() => removeTag(tag)}
                        >
                          x
                        </span>
                      </div>
                    ))}
                    <input
                      onKeyDown={addTag}
                      className="border-none flex-1 outline-none text-black"
                      placeholder="What are your interests?"
                      style={{ backgroundColor: 'transparent' }}
                    />
                  </div>
                </div>
              </div>

              <div className="flex items-center justify-center w-1/4">
                <Button variant="contained" endIcon={<RecommendIcon />} onClick={handleSubmit}>
                  Get Recommendations
                </Button>
              </div>
            </div>
          </div>

          {/* Render recommendations only if showCards is true */}
          {showCards && (
            <div className="flex flex-wrap justify-center gap-4 mt-4">
              {recommendations.map((destination, index) => (
                <Card key={index} onClick={() => handleCardClick(destination)} className="cursor-pointer p-4">
                  <h2>{destination.name}</h2>
                </Card>
              ))}
            </div>
          )}

          {/* Dialog for selected destination */}
          <Dialog open={selectedDestination !== null} onClose={handleCloseDialog}>
            <DialogTitle>{selectedDestination && selectedDestination.name}</DialogTitle>
            <DialogContent>
              {/* You can add more details about the selected destination here */}
            </DialogContent>
            <DialogActions>
              <Button onClick={handleCloseDialog}>Close</Button>
            </DialogActions>
          </Dialog>
        </div>
      </div>
    </div>
  );
}

export default Home;
