import React, { useState } from 'react';
import axios from 'axios';

const Recommend = () => { // Changed function name to start with uppercase
    const [userInput, setUserInput] = useState('');
    const [recommendations, setRecommendations] = useState([]);

    const handleChange = (e) => {
      setUserInput(e.target.value);
    };

    const handleSubmit = async (e) => {
        console.log(us)
      e.preventDefault();
      try {
        const response = await axios.post('http://localhost:6000/recommend', {
          interests: userInput
        });
        setRecommendations(response.data.recommendations);
      } catch (error) {
        console.error('Error fetching recommendations:', error);
      }
    };

    return (
      <div className="recommend">
        <h1>Travel Destination Recommender</h1>
        <form onSubmit={handleSubmit}>
          <label htmlFor="interests">Enter your interests:</label>
          <textarea
            id="interests"
            rows={4} // Set the number of rows to 4
            value={userInput}
            onChange={handleChange}
          />
          <button type="submit">Get Recommendations</button>
        </form>
        <div>
          <h2>Recommended Destinations:</h2>
          <ul>
            {recommendations.map((destination, index) => (
              <li key={index}>{destination}</li>
            ))}
          </ul>
        </div>
      </div>
    );
}

export default Recommend;
